import React, { useContext } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { AuthContext } from '../context/AuthContext'; // Import the AuthContext

const AuthModal = () => {
  const { showModal, setShowModal } = useContext(AuthContext); // Access modal state from context

  const handleClose = () => {
    setShowModal(false); // Set showModal to false to close the modal
  };

  return (
    <Modal show={showModal} onHide={handleClose}>
      {/* Modal content */}
      <Modal.Header closeButton>
        <Modal.Title>Sign In</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {/* Your sign-in form and Google sign-in button */}
        {/* Ensure that the sign-in button is properly configured for Google sign-in */}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default AuthModal;
