import React, { useState } from "react";
import { Auth } from "../Components/Auth"; // Import Auth component correctly
import { db, auth, storage } from "./config/firebase";
import {
  getDocs,
  collection,
  addDoc,
  deleteDoc,
  updateDoc,
  doc,
} from "firebase/firestore";
import { ref, uploadBytes } from "firebase/storage";
import { Button, Form, InputGroup } from "react-bootstrap";

function App() {
  const [commentList, setCommentList] = useState([]);
  const [newCommentContent, setNewCommentContent] = useState("");
  const [isNewCommentImportant, setIsNewCommentImportant] = useState(false);
  const [updatedCommentContent, setUpdatedCommentContent] = useState("");
  const [fileUpload, setFileUpload] = useState(null);

  const commentsCollectionRef = collection(db, "comments");

  const getCommentList = async () => {
    try {
      const data = await getDocs(commentsCollectionRef);
      const filteredData = data.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      setCommentList(filteredData);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    getCommentList();
  }, []);

  const onSubmitComment = async () => {
    try {
      await addDoc(commentsCollectionRef, {
        content: newCommentContent,
        important: isNewCommentImportant,
        userId: auth?.currentUser?.uid,
      });
      getCommentList();
    } catch (err) {
      console.error(err);
    }
  };

  const deleteComment = async (id) => {
    const commentDoc = doc(db, "comments", id);
    await deleteDoc(commentDoc);
    getCommentList();
  };

  const updateCommentContent = async (id) => {
    const commentDoc = doc(db, "comments", id);
    await updateDoc(commentDoc, { content: updatedCommentContent });
    getCommentList();
  };

  const uploadFile = async () => {
    if (!fileUpload) return;
    const filesFolderRef = ref(storage, `projectFiles/${fileUpload.name}`);
    try {
      await uploadBytes(filesFolderRef, fileUpload);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="App">
      <Auth /> {/* Render the Auth component */}

      {/* Rest of your App component */}
    </div>
  );
}

export default App;
