import React, { useEffect, useState } from "react";
import firebase from "./firebaseConfig.js"; // Import your firebase.js file

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    // Add a listener to watch for changes in authentication state
    const unsubscribe = firebase.auth().onAuthStateChanged((user) => {
      if (user) {
        // User is signed in.
        setUser(user);
      } else {
        // User is signed out.
        setUser(null);
      }
    });

    // Clean up the listener when the component unmounts
    return () => unsubscribe();
  }, []);

  return (
    <div>
      <h1>React Firebase App</h1>
      {user ? <p>Welcome, {user.displayName}!</p> : <p>Please sign in.</p>}
      {/* Add your authentication or Firestore components here */}
    </div>
  );
}

export default App;
