import firebase from 'firebase/app';
import 'firebase/auth'; // If you need authentication
import 'firebase/firestore'; // If you need Firestore
import 'firebase/storage'; // If you need Cloud Storage

const firebaseConfig = {
  apiKey: 'YOUR_API_KEY',
  authDomain: 'YOUR_AUTH_DOMAIN',
  projectId: 'YOUR_PROJECT_ID',
  storageBucket: 'YOUR_STORAGE_BUCKET',
  messagingSenderId: 'YOUR_MESSAGING_SENDER_ID',
  appId: 'YOUR_APP_ID',
};

const app = firebase.initializeApp(firebaseConfig);

export const auth = app.auth(); // If you need authentication
export const firestore = app.firestore(); // If you need Firestore
export const storage = app.storage(); // If you need Cloud Storage

export default app;
