import React, { useContext, useEffect, useState } from "react";
import {
  Button,
  Form,
  Container,
  Row,
  Col,
  Card,
  Image,
} from "react-bootstrap";

import {
  signInWithEmailAndPassword,
  signInWithPopup,
  signOut,
} from "firebase/auth";

import { useNavigate } from "react-router-dom";

import { UserProvider } from "../providers/UserProvider";

import { auth, googleProvider, db } from "../config/firebase";

import axios from "axios";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  // const [user, setUser] = useState(null);
  const [userProfile, setUserProfile] = useState({});
  const [userComments, setUserComments] = useState([]);
  const [userThreads, setUserThreads] = useState([]);
  const [error, setError] = useState("");

  const navigate = useNavigate();
  const user = useContext(UserProvider);

  useEffect(() => {
    // Here you would check for a valid JWT in cookies/localStorage,
    // and if one is found, fetch the user data from your backend
  }, []);
  console.log(user);
  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        try {
          const response = await axios.get(`/api/users/${user.id}`);

          setUserProfile(response.data);

          const commentsResponse = await axios.get(
            `/api/users/${user.id}/comments`
          );
          setUserComments(commentsResponse.data);

          const threadsResponse = await axios.get(
            `/api/users/${user.id}/threads`
          );
          setUserThreads(threadsResponse.data);
        } catch (error) {
          console.error("Error fetching user data:", error);
        }
      }
    };

    fetchUserData();
  }, [user]);

  const handleSignIn = async () => {
    try {
      const loginResponse = await signInWithEmailAndPassword(
        auth,
        email,
        password
      );

      // redirect to landing page
      navigate("/landing");
      // setUser(response.data);
    } catch (error) {
      console.log(error.message);
      setError(error.message);
      // console.error("Error signing in:", error);
    }
  };

  const handleSignInWithGoogle = async () => {
    try {
      // await signInWithPopup(auth, googleProvider);
      signInWithPopup(auth, googleProvider).then((res) => {
        const user = res.user;
        setUser(user);
        navigate("/landing");
      });
    } catch (error) {
      console.error("Error signing in with Google:", error);
    }
  };

  const handleSignOut = async () => {
    try {
      await axios.post("/api/auth/logout");
      // setUser(null);
      navigate("/");
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  return (
    <Container fluid className="p-0">
      <Row noGutters className="min-vh-100">
        <Col
          md={6}
          className="d-flex align-items-center justify-content-center p-5"
        >
          <Card className="w-100 p-4">
            <Card.Title className="text-center">
              <Image
                src="./assets/ProgradeLogo.png"
                roundedCircle
                width="80"
                height="80"
                className="mb-3"
              />
              Welcome to Prograde
            </Card.Title>
            {error && <div className="login-error">{error}</div>}
            {user ? (
              <>
                <img src={userProfile.profilePhoto} alt="Profile" />
                <h3>Your Comments:</h3>
                <ul>
                  {userComments.map((comment, index) => (
                    <li key={index}>{comment.text}</li>
                  ))}
                </ul>
                <h3>Your Threads:</h3>
                <ul>
                  {userThreads.map((thread, index) => (
                    <li key={index}>{thread.title}</li>
                  ))}
                </ul>
                <Button variant="danger" onClick={handleSignOut}>
                  Logout
                </Button>
              </>
            ) : (
              <Form>
                <Form.Group controlId="email">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="Enter email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Form.Group>

                <Form.Group controlId="password">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </Form.Group>
                <Button variant="secondary" onClick={handleSignIn}>
                  Sign In
                </Button>
                <Button
                  variant="primary"
                  onClick={handleSignInWithGoogle}
                  className="ml-2"
                >
                  Sign In with Google
                </Button>
              </Form>
            )}
          </Card>
        </Col>
        <Col md={6} className="d-none d-md-block p-0">
          <Image src="./assets/commute.jpg" alt="Commute Image" fluid />
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
