import React, { useState } from "react";
import { auth, googleProvider } from "../config/firebase";
import { createUserWithEmailAndPassword, signInWithPopup, signOut } from "firebase/auth";

export const Auth = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState(null); // Add an error state

  const signIn = async (e) => {
    e.preventDefault(); // Prevent the form from submitting
    try {
      await createUserWithEmailAndPassword(auth, email, password);
      // Clear the form fields on successful sign-in
      setEmail("");
      setPassword("");
      setError(null); // Clear any previous errors
    } catch (err) {
      setError(err.message); // Display the error message
    }
  };

  const signInWithGoogle = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      setError(null); // Clear any previous errors
    } catch (err) {
      setError(err.message); // Display the error message
    }
  };

  const logout = async () => {
    try {
      await signOut(auth);
      setError(null); // Clear any previous errors
    } catch (err) {
      setError(err.message); // Display the error message
    }
  };

  return (
    <div>
      <form onSubmit={signIn}> {/* Use onSubmit for the form */}
        <input
          placeholder="Email..."
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <input
          placeholder="Password..."
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit"> Sign In</button> {/* Use type="submit" for the button */}
      </form>
      <p style={{ color: "red" }}>{error}</p> {/* Display error message */}
      <button onClick={signInWithGoogle}> Sign In With Google</button>
      <button onClick={logout}> Logout </button>
    </div>
  );
};
