import React, { useState } from 'react';
import { Button, InputGroup, Form, Card } from 'react-bootstrap';
import { updateDoc, deleteDoc, doc } from 'firebase/firestore';
import { db } from './config/firebase';

const Comment = ({ comment, onDelete, onUpdate }) => {
  const [updatedCommentContent, setUpdatedCommentContent] = useState(comment.text);

  const handleUpdateComment = async () => {
    const commentDoc = doc(db, "comments", comment.id);
    try {
      await updateDoc(commentDoc, { text: updatedCommentContent });
      onUpdate(comment.id, updatedCommentContent);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Card key={comment.id}>
      <Card.Body>
        <Card.Title>{comment.text}</Card.Title>
        <Card.Text>Likes: {comment.likes}</Card.Text>
        <Button onClick={() => onDelete(comment.id)}>Delete Comment</Button>
        <InputGroup className="mb-3">
          <Form.Control
            placeholder="Update comment..."
            value={updatedCommentContent}
            onChange={(e) => setUpdatedCommentContent(e.target.value)}
          />
          <Button onClick={handleUpdateComment}>Update Comment</Button>
        </InputGroup>
      </Card.Body>
    </Card>
  );
};

export default Comment;
