import React, { useState, useEffect } from "react";
import Navbar from "./Components/Navbar";
import Home from "./Pages/Home";
import Community from "./Pages/Community";
import FindRoute from "./Pages/FindRoute";
import Login from "./Pages/Login";
import Profile from "./Components/UserProfile";
import Settings from "./Pages/Settings";
import StationInfo from "./Pages/StationInfo";
import LandingPage from "./Pages/LandingPage";
import ThreadList from "./Components/ThreadList";
import AuthModal from "./Components/AuthModal";
import UpdateProfile from "./Pages/UpdateProfile";
import { AuthProvider, useAuth } from "./context/AuthContext";
import { UserProvider } from "./context/UserContext";
import { Routes, Route } from "react-router-dom";

import "./App.css";

function App() {
  const [user, setUser] = useState(null);
  const { currentUser } = useAuth();

  useEffect(() => {
    if (currentUser && !user) {
      // Assuming 'generateUserProfile' is a function that fetches/creates a user profile
      // Replace it with the appropriate function to initialize the user's profile
      generateUserProfile(currentUser.uid).then(profile => {
        setUser(profile);
      });
    }
  }, [currentUser]);

  return (
    <div className="App">
      <AuthProvider>
        <UserProvider value={{ user, setUser }}>
          <Navbar />
          <Routes>
            <Route path="/" element={<Login />} />
            <Route path="/home" element={<Home />} />
            <Route path="/community" element={<Community />} />
            <Route path="/find-route" element={<FindRoute />} />
            <Route path="/landing" element={<LandingPage />} />
            <Route path="/profile" element={<Profile />} />
            <Route path="/settings" element={<Settings />} />
            <Route path="/station-info" element={<StationInfo />} />
            <Route path="/threads" element={<ThreadList />} />
            {currentUser && <Route path="/update-profile" element={<UpdateProfile />} />}
            <Route path="*" element={<div>404 Not Found</div>} />
          </Routes>
          <AuthModal />
        </UserProvider>
      </AuthProvider>
    </div>
  );
}

export default App;

async function generateUserProfile(uid) {
  try {
    const response = await fetch(`/api/users/${uid}`);
    if (response.ok) {
      const data = await response.json();
      return data.user;
    } else {
      // Handle error gracefully, maybe redirect to a setup profile page
      console.error('Error fetching user profile:', await response.text());
    }
  } catch (error) {
    console.error('Error fetching user profile:', error);
  }
  return null;
}
