import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { signInWithEmailAndPassword, signInWithPopup, signOut } from "firebase/auth";
import { auth, googleProvider, db } from "../config/firebase";
import { doc, getDoc, collection, query, where, getDocs } from "firebase/firestore";

const AuthModal = ({ show, onClose }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);
  const [userProfile, setUserProfile] = useState({});
  const [userComments, setUserComments] = useState([]);
  const [userThreads, setUserThreads] = useState([]);

  useEffect(() => {
    auth.onAuthStateChanged((authUser) => {
      if (authUser) {
        setUser(authUser);
      } else {
        setUser(null);
      }
    });
  }, []);

  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        const userRef = doc(db, "users", user.uid);
        const userDoc = await getDoc(userRef);
        if (userDoc.exists()) {
          setUserProfile(userDoc.data());
        }

        const commentsRef = collection(db, "comments");
        const commentsQuery = query(commentsRef, where("userId", "==", user.uid));
        const commentsSnapshot = await getDocs(commentsQuery);
        setUserComments(commentsSnapshot.docs.map(doc => doc.data()));

        const threadsRef = collection(db, "threads");
        const threadsQuery = query(threadsRef, where("userId", "==", user.uid));
        const threadsSnapshot = await getDocs(threadsQuery);
        setUserThreads(threadsSnapshot.docs.map(doc => doc.data()));
      }
    };

    fetchUserData();
  }, [user]);

  const handleSignIn = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      onClose();
    } catch (error) {
      console.error("Error signing in:", error);
    }
  };

  const handleSignInWithGoogle = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      onClose();
    } catch (error) {
      console.error("Error signing in with Google:", error);
    }
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
      onClose();
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };


return (
  <Modal show={show} onHide={onClose}>
    <Modal.Header closeButton>
      <Modal.Title>{user ? `We're sad to see you go, ${user.email.split('@')[0]}` : "Sign In"}</Modal.Title>
    </Modal.Header>
    <Modal.Body>
      {user ? (
        <>
          <p>
            We noticed how much you love engaging with our community! Before you leave, remember that we have some cool and cute features just around the corner! üåà
          </p>
          <p>
            If you decide to leave, we hope to see you back soon! Remember, Prograde is always here to bring a little sunshine to your day! ‚òÄÔ∏è
          </p>
          <img src={userProfile.profilePhoto} alt="Profile" className="img-fluid rounded-circle my-3" style={{maxWidth: '100px'}} />
          <h3>Your Recent Comments:</h3>
          <ul>
            {userComments.slice(0, 3).map((comment, index) => (
              <li key={index}>{comment.text}</li>
            ))}
          </ul>
          <h3>Your Recent Threads:</h3>
          <ul>
            {userThreads.slice(0, 3).map((thread, index) => (
              <li key={index}>{thread.title}</li>
            ))}
          </ul>
        </>
      ) : (
        <Form>
          {/* ... (unchanged code) */}
        </Form>
      )}
    </Modal.Body>
    <Modal.Footer>
      {user ? (
        <Button variant="danger" onClick={handleSignOut}>
          Confirm Logout
        </Button>
      ) : (
        <>
          {/* ... (unchanged code) */}
        </>
      )}
    </Modal.Footer>
  </Modal>
);};

export default AuthModal;
