import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import { signInWithEmailAndPassword, signInWithPopup, signOut } from "firebase/auth"; // import signOut
import { auth, googleProvider } from "../config/firebase";

const AuthModal = ({ show, onClose, isAuthenticated }) => {  // Add isAuthenticated prop
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSignIn = async () => {
    try {
      await signInWithEmailAndPassword(auth, email, password);
      onClose();
    } catch (error) {
      console.error("Error signing in:", error);
    }
  };

  const handleSignInWithGoogle = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
      onClose();
    } catch (error) {
      console.error("Error signing in with Google:", error);
    }
  };

  const handleSignOut = async () => {  // New logout handler
    try {
      await signOut(auth);
      onClose();
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  return (
    <Modal show={show} onHide={onClose}>
      <Modal.Header closeButton>
        <Modal.Title>{ isAuthenticated ? "Logout" : "Sign In" }</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        { isAuthenticated ? (
          <p>Are you sure you want to logout?</p>
        ) : (
          <Form>
            <Form.Group controlId="email">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>

            <Form.Group controlId="password">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
          </Form>
        )}
      </Modal.Body>
      <Modal.Footer>
        { isAuthenticated ? (
          <Button variant="danger" onClick={handleSignOut}>
            Logout
          </Button>
        ) : (
          <>
            <Button variant="secondary" onClick={handleSignIn}>
              Sign In
            </Button>
            <Button variant="primary" onClick={handleSignInWithGoogle}>
              Sign In with Google
            </Button>
          </>
        )}
      </Modal.Footer>
    </Modal>
  );
};

export default AuthModal;
