import { useEffect, useState } from "react";
import "./App.css";
import { Auth } from "./components/Auth";
import { db, auth, storage } from "./config/firebase";
import {
  getDocs,
  collection,
  addDoc,
  deleteDoc,
  updateDoc,
  doc,
} from "firebase/firestore";
import { ref, uploadBytes } from "firebase/storage";

function App() {
  const [progradeList, setProgradeList] = useState([]);

  // New Prograde States
  const [newProgradeContent, setNewProgradeContent] = useState("");
  const [isNewProgradeImportant, setIsNewProgradeImportant] = useState(false);

  // Update Prograde Content State
  const [updatedProgradeContent, setUpdatedProgradeContent] = useState("");

  // File Upload State
  const [fileUpload, setFileUpload] = useState(null);

  const progradesCollectionRef = collection(db, "progrades");

  const getProgradeList = async () => {
    try {
      const data = await getDocs(progradesCollectionRef);
      const filteredData = data.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      setProgradeList(filteredData);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    getProgradeList();
  }, []);

  const onSubmitPrograde = async () => {
    try {
      await addDoc(progradesCollectionRef, {
        content: newProgradeContent,
        important: isNewProgradeImportant,
        userId: auth?.currentUser?.uid,
      });
      getProgradeList();
    } catch (err) {
      console.error(err);
    }
  };

  const deletePrograde = async (id) => {
    const progradeDoc = doc(db, "progrades", id);
    await deleteDoc(progradeDoc);
  };

  const updateProgradeContent = async (id) => {
    const progradeDoc = doc(db, "progrades", id);
    await updateDoc(progradeDoc, { content: updatedProgradeContent });
  };

  const uploadFile = async () => {
    if (!fileUpload) return;
    const filesFolderRef = ref(storage, `projectFiles/${fileUpload.name}`);
    try {
      await uploadBytes(filesFolderRef, fileUpload);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="App">
      <Auth />

      <div>
        <input
          placeholder="Prograde content..."
          onChange={(e) => setNewProgradeContent(e.target.value)}
        />
        <input
          type="checkbox"
          checked={isNewProgradeImportant}
          onChange={(e) => setIsNewProgradeImportant(e.target.checked)}
        />
        <label> Important</label>
        <button onClick={onSubmitPrograde}> Submit Prograde</button>
      </div>
      <div>
        {progradeList.map((prograde) => (
          <div>
            <h1 style={{ color: prograde.important ? "red" : "green" }}>
              {prograde.content}
            </h1>

            <button onClick={() => deletePrograde(prograde.id)}>
              Delete Prograde
            </button>

            <input
              placeholder="New content..."
              onChange={(e) => setUpdatedProgradeContent(e.target.value)}
            />
            <button onClick={() => updateProgradeContent(prograde.id)}>
              Update Prograde Content
            </button>
          </div>
        ))}
      </div>

      <div>
        <input type="file" onChange={(e) => setFileUpload(e.target.files[0])} />
        <button onClick={uploadFile}> Upload File </button>
      </div>
    </div>
  );
}

export default App;
