import React, { useState, useEffect } from 'react';
import { db, auth } from '../config/firebase'; 
import { doc, getDoc, setDoc, collection, query, where, getDocs } from "firebase/firestore";
import { Button, Form } from 'react-bootstrap';

const UserProfile = () => {
  const [userData, setUserData] = useState({});
  const [userComments, setUserComments] = useState([]);
  const [userThreads, setUserThreads] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const user = auth.currentUser;
        if (user) {
          const userRef = doc(db, 'users', user.uid);
          const userDoc = await getDoc(userRef);
          if (userDoc.exists()) {
            setUserData(userDoc.data());

            // Fetch user comments
            const commentsRef = collection(db, "comments");
            const commentsQuery = query(commentsRef, where("userId", "==", user.uid));
            const commentsSnapshot = await getDocs(commentsQuery);
            setUserComments(commentsSnapshot.docs.map(doc => doc.data()));

            // Fetch user threads
            const threadsRef = collection(db, "threads");
            const threadsQuery = query(threadsRef, where("userId", "==", user.uid));
            const threadsSnapshot = await getDocs(threadsQuery);
            setUserThreads(threadsSnapshot.docs.map(doc => doc.data()));

            setIsLoading(false);
          }
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    fetchUserData();
  }, []);

  const handleUpdateProfile = async () => {
    try {
      const user = auth.currentUser;
      if (user) {
        const userRef = doc(db, 'users', user.uid);
        await setDoc(userRef, userData, { merge: true });
        alert('Profile updated successfully!');
      }
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  }

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="UserProfile">
      <h1>Your Profile</h1>
      <img src={userData.profilePhoto} alt="Profile" />

      <Form>
        <Form.Group controlId="username">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            value={userData.username || ''}
            onChange={(e) => setUserData(prevState => ({ ...prevState, username: e.target.value }))}
          />
        </Form.Group>

        {/* Add more fields as necessary, like email, etc. */}

        <Button variant="primary" onClick={handleUpdateProfile}>
          Update Profile
        </Button>
      </Form>

      <h3>Your Comments:</h3>
      <ul>
        {userComments.map((comment, index) => (
          <li key={index}>{comment.text}</li>
        ))}
      </ul>

      <h3>Your Threads:</h3>
      <ul>
        {userThreads.map((thread, index) => (
          <li key={index}>{thread.title}</li>
        ))}
      </ul>
    </div>
  );
}

export default UserProfile;
