import React, { useState } from "react";
import { Navbar, Nav, Container, Image, Button } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import AuthModal from "./AuthModal";
import { useUser } from "../context/UserContext";
import { signOut } from "firebase/auth";
import { auth } from "../config/firebase";

const MyNavbar = () => {
  const progradeLogoPath = "./assets/ProgradeLogo.png";

  const [showAuthModal, setShowAuthModal] = useState(false);
  const { user, userProfile } = useUser();

  const openAuthModal = () => {
    setShowAuthModal(true);
  };

  const closeAuthModal = () => {
    setShowAuthModal(false);
  };

  const handleSignOut = async () => {
    try {
      await signOut(auth);
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg">
      <Container>
        <LinkContainer to="/">
          <Navbar.Brand>
            <Image
              src={progradeLogoPath}
              roundedCircle
              width="40"
              height="40"
              className="mr-2"
            />
            Prograde
          </Navbar.Brand>
        </LinkContainer>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            {/* ... (same as your existing links) ... */}
          </Nav>
          {user && (
            <Navbar.Text className="mr-3">
              Signed in as: {user.email}
            </Navbar.Text>
          )}
          {user ? (
            <Image
              src={userProfile?.profilePhoto || 'path/to/your/default/avatar.png'}
              roundedCircle
              width="40"
              height="40"
              className="mr-2"
            />
          ) : (
            <Button variant="outline-success" onClick={openAuthModal}>
              Login
            </Button>
          )}
          {user && (
            <Button variant="outline-info" onClick={handleSignOut}>
              Logout
            </Button>
          )}
        </Navbar.Collapse>
      </Container>
      <AuthModal show={showAuthModal} onClose={closeAuthModal} />
    </Navbar>
  );
};

export default MyNavbar;
