import React from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';

const Settings = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleClick = (path) => {
    navigate(path);
  };

  const isActive = (path) => {
    return location.pathname.includes(path);
  };

  return (
    <div style={{ padding: '20px', display: 'flex' }}>
      <aside style={{ width: '250px', borderRight: '1px solid #ccc' }}>
        <h2 onClick={() => handleClick('user-profile')} style={{ backgroundColor: isActive('user-profile') ? '#f0f0f0' : 'transparent', padding: '10px', cursor: 'pointer' }}>
          <i className="material-icons">account_circle</i> User Profile
        </h2>
        <h2 onClick={() => handleClick('notifications')} style={{ backgroundColor: isActive('notifications') ? '#f0f0f0' : 'transparent', padding: '10px', cursor: 'pointer' }}>
          <i className="material-icons">notifications</i> Notifications
        </h2>
        <h2 onClick={() => handleClick('privacy')} style={{ backgroundColor: isActive('privacy') ? '#f0f0f0' : 'transparent', padding: '10px', cursor: 'pointer' }}>
          <i className="material-icons">privacy_tip</i> Privacy
        </h2>
        <h2 onClick={() => handleClick('support')} style={{ backgroundColor: isActive('support') ? '#f0f0f0' : 'transparent', padding: '10px', cursor: 'pointer' }}>
          <i className="material-icons">help_outline</i> Support
        </h2>
      </aside>
      <div style={{ flex: 1, marginLeft: '20px' }}>
        <Routes>
          <Route path="user-profile" element={<UserProfile />} />
          <Route path="notifications" element={<Notifications />} />
          <Route path="privacy" element={<Privacy />} />
          <Route path="support" element={<Support />} />
        </Routes>
      </div>
    </div>
  );
};

const UserProfile = () => (
  <div>
    <h2>User Profile</h2>
    {/* ... user profile content ... */}
  </div>
);

const Notifications = () => (
  <div>
    <h2>Notifications</h2>
    {/* ... notifications content ... */}
  </div>
);

const Privacy = () => (
  <div>
    <h2>Privacy</h2>
    {/* ... privacy content ... */}
  </div>
);

const Support = () => (
  <div>
    <h2>Support</h2>
    {/* ... support content ... */}
  </div>
);

export default Settings;
