import React from 'react';
import { Routes, Route, useNavigate, useLocation } from 'react-router-dom';

const Settings = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleClick = (path) => {
    navigate(path);
  };

  const isActive = (path) => {
    return location.pathname.includes(path);
  };

  return (
    <div style={{ padding: '20px', display: 'flex' }}>
      <aside style={{ width: '250px', borderRight: '1px solid #ccc' }}>
        {/* ... sidebar content (with the handleClick and isActive functions used) ... */}
        <h2 onClick={() => handleClick('user-profile')} style={{ backgroundColor: isActive('user-profile') ? '#f0f0f0' : 'transparent' }}>
          <i className="material-icons">account_circle</i> User Profile
        </h2>
        {/* ... more sidebar content ... */}
      </aside>
      <div style={{ flex: 1, marginLeft: '20px' }}>
        <Routes>
          <Route path="user-profile" element={<UserProfile />} />
          {/* ... other routes ... */}
        </Routes>
      </div>
    </div>
  );
};

const UserProfile = () => (
  <div>
    <h2>User Profile</h2>
    {/* ... user profile content ... */}
  </div>
);

export default Settings;
