import React from 'react';
import { Container, Button } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';

const LandingPage = () => {
  const navigate = useNavigate();

  return (
    <div className="position-relative" style={{ height: 'calc(100vh - 60px)' }}> {/* Adjust 60px to your navbar height */}
     <video className="position-fixed top-0 start-0 w-100 h-100 object-cover" playsInline autoPlay muted loop style={{ objectFit: 'cover' }}>
  <source src="assets/bkview.mp4" type="video/mp4" />
  Your browser does not support the video tag.
</video>

      <Container 
        className="position-absolute top-50 start-50 translate-middle d-flex flex-column justify-content-center align-items-center bg-opacity-75 bg-dark text-white p-4" 
        style={{ 
          borderRadius: '15px', 
          maxWidth: '600px', 
          textAlign: 'center', 
          zIndex: 1,
        }}
      >
        <h1>Welcome to Prograde!</h1>
        <p>
          Prograde is a revolutionary platform that brings productivity and grading to a new level. Dive in to experience a seamless and intuitive environment tailored to meet all your needs. Discover, learn, and grow with Prograde.
        </p>
        <Button variant="primary" onClick={() => navigate('/community')}>
          Check Out the Community
        </Button>
      </Container>
    </div>
  );
};

export default LandingPage;
