import React, { useContext } from 'react';
import { Navbar, Nav, Container, Image } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import { AuthContext } from '../context/AuthContext'; // Adjust the import path based on your directory structure

const MyNavbar = () => {
    const progradeLogoPath = "./assets/ProgradeLogo.png";
    const { user } = useContext(AuthContext); // Access user from context

    return (
        <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
                <LinkContainer to="/">
                    <Navbar.Brand>
                        <Image src={progradeLogoPath} roundedCircle width="40" height="40" className="mr-2" />
                        Prograde
                    </Navbar.Brand>
                </LinkContainer>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        {/* ... other links ... */}
                    </Nav>
                    {user && (
                        <Nav>
                            <Nav.Item className="mr-3">
                                <p className="text-light m-0">Logged in as:</p>
                                <p className="text-light m-0">{user.email}</p>
                            </Nav.Item>
                            {/* If you have a user profile image, you can add it here */}
                        </Nav>
                    )}
                </Navbar.Collapse>
            </Container>
        </Navbar>
    )
}

export default MyNavbar;
