import React, { useState } from 'react';
import { Navbar, Nav, Container, Image } from 'react-bootstrap';
import { LinkContainer } from 'react-router-bootstrap';
import AuthModal from './AuthModal'; // Import the AuthModal component
import UserContext from '../context/UserContext';  // Adjust the path to your actual UserContext file


const MyNavbar = () => {
    // Path to the Prograde logo in your internal assets. Replace `.extension` with actual file extension
    const progradeLogoPath = "./assets/ProgradeLogo.png";


    const [showAuthModal, setShowAuthModal] = useState(false); // State to control the visibility of the auth modal
    const { user } = useContext(UserContext);


    const openAuthModal = () => {
        setShowAuthModal(true);
    };

    const closeAuthModal = () => {
        setShowAuthModal(false);
    };

    return (
        <Navbar bg="dark" variant="dark" expand="lg">
            <Container>
                <LinkContainer to="/">
                    <Navbar.Brand>
                        <Image src={progradeLogoPath} roundedCircle width="40" height="40" className="mr-2" />
                        Prograde
                    </Navbar.Brand>
                </LinkContainer>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="mr-auto">
                        <LinkContainer to="/home">
                            <Nav.Link>Home</Nav.Link>
                        </LinkContainer>
                        <LinkContainer to="/community">
                            <Nav.Link>Community</Nav.Link>
                        </LinkContainer>
                        <LinkContainer to="/find-route">
                            <Nav.Link>Find Route</Nav.Link>
                        </LinkContainer>
                        <Nav.Link onClick={openAuthModal}>Login</Nav.Link> {/* Open the auth modal on click */}
                        <LinkContainer to="/profile">
                            <Nav.Link>Profile</Nav.Link>
                        </LinkContainer>
                        <LinkContainer to="/settings">
                            <Nav.Link>Settings</Nav.Link>
                        </LinkContainer>
                        <LinkContainer to="/station-info">
                            <Nav.Link>Station Info</Nav.Link>
                        </LinkContainer>
                    </Nav>
                    {/* If you have a user profile image, you can uncomment and use the following section */}
                    {/* <Nav>
                        <Image src={userProfileImage} roundedCircle width="40" height="40" />
                    </Nav> */}
                </Navbar.Collapse>
            </Container>
            {/* Pass showAuthModal and closeAuthModal to AuthModal */}
            <AuthModal show={showAuthModal} handleClose={closeAuthModal} />
        </Navbar>
    )
}

export default MyNavbar;
