import React, { useState, useEffect } from 'react';
import { auth } from '../config/firebase'; 
import { Button, Form } from 'react-bootstrap';
import { useHistory } from 'react-router-dom'; // Import useHistory hook

const UserProfile = () => {
  const [userData, setUserData] = useState(auth.currentUser || {});
  const [userComments, setUserComments] = useState([]);
  const [userThreads, setUserThreads] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const history = useHistory(); // Instantiate useHistory hook

  useEffect(() => {
    const fetchUserDataFromBackend = async () => {
      const user = auth.currentUser;
      if (user) {
        try {
          const response = await fetch(`/api/users/${user.uid}`);
          const data = await response.json();

          if (data.status === 200) {
            setUserData(data.data);
          } else {
            console.error('Error fetching user data:', data.error);
          }

          setIsLoading(false);
        } catch (error) {
          console.error("Error fetching user data from backend:", error);
        }
      }
    };

    fetchUserDataFromBackend();
  }, []);

  // Function to navigate to the update profile route
  const handleGoToUpdateProfile = () => {
    history.push('/update-profile');
  };

  return (
    <div className="UserProfile">
      <h1>Your Profile</h1>
      <img src={userData.profilePhoto || 'defaultProfilePhotoURL'} alt="Profile" />

      <Form>
        <Form.Group controlId="name">
          <Form.Label>Name</Form.Label>
          <Form.Control
            type="text"
            value={userData.name || ''}
            onChange={(e) => setUserData(prevState => ({ ...prevState, name: e.target.value }))}
          />
        </Form.Group>

        <Form.Group controlId="email">
          <Form.Label>Email</Form.Label>
          <Form.Control
            type="email"
            value={userData.email || ''}
            onChange={(e) => setUserData(prevState => ({ ...prevState, email: e.target.value }))}
          />
        </Form.Group>

        {/* Add more fields as necessary, like profile photo URL, etc. */}

        <Button variant="primary" onClick={handleGoToUpdateProfile}>
          Go to Update Profile
        </Button>
      </Form>

      {isLoading ? (
        <div>Loading comments and threads...</div>
      ) : (
        <>
          <h3>Your Comments:</h3>
          <ul>
            {userComments.map((comment, index) => (
              <li key={index}>{comment.text}</li>
            ))}
          </ul>

          <h3>Your Threads:</h3>
          <ul>
            {userThreads.map((thread, index) => (
              <li key={index}>{thread.title}</li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

export default UserProfile;
