import React, { useEffect, useState } from "react";
import "./App.css";
import { Auth } from "./Components/Auth";
import { db, auth, storage } from "./config/firebase";
import {
  getDocs,
  collection,
  addDoc,
  deleteDoc,
  updateDoc,
  doc,
} from "firebase/firestore";
import { ref, uploadBytes } from "firebase/storage";
import { Button, Form, InputGroup, Card } from "react-bootstrap";
import { Link } from "react-router-dom"; // Import Link from react-router-dom

function App() {
  const [commentList, setCommentList] = useState([]);
  const [newCommentContent, setNewCommentContent] = useState("");
  const [isNewCommentImportant, setIsNewCommentImportant] = useState(false);
  const [updatedCommentContent, setUpdatedCommentContent] = useState("");
  const [fileUpload, setFileUpload] = useState(tr);

  const commentsCollectionRef = collection(db, "comments");

  const fetchCommentList = async () => {
    try {
      const data = await getDocs(commentsCollectionRef);
      const filteredData = data.docs.map((doc) => ({
        ...doc.data(),
        id: doc.id,
      }));
      setCommentList(filteredData);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchCommentList();
  }, []);

  const onSubmitComment = async () => {
    const user = auth.currentUser;

    if (user) {
      const userId = user.uid;

      try {
        if (newCommentContent.trim() === "") {
          console.error("Comment content is empty.");
          return;
        }

        await addDoc(commentsCollectionRef, {
          content: newCommentContent,
          important: isNewCommentImportant,
          userId: userId,
        });

        // Optimized: Only add the new comment to the list instead of fetching all.
        setCommentList((prevCommentList) => [
          ...prevCommentList,
          {
            content: newCommentContent,
            important: isNewCommentImportant,
            userId: userId,
          },
        ]);

        // Clear the input fields
        setNewCommentContent("");
        setIsNewCommentImportant(false);
      } catch (err) {
        console.error(err);
      }
    } else {
      console.error("User is not authenticated.");
    }
  };

  const deleteComment = async (id) => {
    const commentDoc = doc(db, "comments", id);
    await deleteDoc(commentDoc);

    // Optimized: Remove the deleted comment from the list.
    setCommentList((prevCommentList) =>
      prevCommentList.filter((comment) => comment.id !== id)
    );
  };

  const updateCommentContent = async (id) => {
    const commentDoc = doc(db, "comments", id);

    try {
      await updateDoc(commentDoc, { content: updatedCommentContent });

      // Optimized: Update the content in the comment list.
      setCommentList((prevCommentList) =>
        prevCommentList.map((comment) =>
          comment.id === id
            ? { ...comment, content: updatedCommentContent }
            : comment
        )
      );
    } catch (err) {
      console.error(err);
    }
  };

  const uploadFile = async () => {
    if (!fileUpload) return;
    const filesFolderRef = ref(storage, `projectFiles/${fileUpload.name}`);

    try {
      await uploadBytes(filesFolderRef, fileUpload);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="App">
      <Auth />

      <div>
        <InputGroup className="mb-3">
          <Form.Control
            placeholder="Comment content..."
            value={newCommentContent}
            onChange={(e) => setNewCommentContent(e.target.value)}
          />
          <InputGroup.Checkbox
            checked={isNewCommentImportant}
            onChange={(e) => setIsNewCommentImportant(e.target.checked)}
          />
          <InputGroup.Text>Important</InputGroup.Text>
        </InputGroup>
        <Button onClick={onSubmitComment}>Submit Comment</Button>

        {/* Add a link to the comments page */}
        <Link to="/comments">View Comments</Link>
      </div>
      <div>
        {commentList.map((comment) => (
          <Card key={comment.id}>
            <Card.Body>
              <Card.Title>{comment.title}</Card.Title>
              <Card.Text>{comment.content}</Card.Text>
              <Button onClick={() => deleteComment(comment.id)}>
                Delete Comment
              </Button>
              <InputGroup className="mb-3">
                <Form.Control
                  placeholder="New content..."
                  value={updatedCommentContent}
                  onChange={(e) => setUpdatedCommentContent(e.target.value)}
                />
                <Button onClick={() => updateCommentContent(comment.id)}>
                  Update Comment Content
                </Button>
              </InputGroup>
            </Card.Body>
          </Card>
        ))}
      </div>
      <div>
        <Form.Control
          type="file"
          onChange={(e) => setFileUpload(e.target.files[0])}
        />
        <Button onClick={uploadFile}>Upload File</Button>
      </div>
    </div>
  );
}

export default App;
