import React, { useState, useEffect } from 'react';
import { db, auth } from './config/firebase'; // Ensure path is correct
import { Button, Form } from 'react-bootstrap';

const UserProfile = () => {
  const [userData, setUserData] = useState({});
  const [isLoading, setIsLoading] = useState(true);

  // Assuming your user's profile is stored in a 'user' collection in Firestore
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const user = auth.currentUser;
        if (user) {
          const userDoc = await db.collection('user').doc(user.uid).get();
          if (userDoc.exists) {
            setUserData(userDoc.data());
            setIsLoading(false);
          }
        }
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    }

    fetchUserData();
  }, []);

  const handleUpdateProfile = async () => {
    try {
      const user = auth.currentUser;
      if (user) {
        await db.collection('user').doc(user.uid).update(userData);
        alert('Profile updated successfully!');
      }
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  }

  if (isLoading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="UserProfile">
      <h1>Your Profile</h1>

      <Form>
        <Form.Group controlId="username">
          <Form.Label>Username</Form.Label>
          <Form.Control
            type="text"
            value={userData.username || ''}
            onChange={(e) => setUserData(prevState => ({ ...prevState, username: e.target.value }))}
          />
        </Form.Group>

        {/* Add more fields as necessary, like email, etc. */}

        <Button variant="primary" onClick={handleUpdateProfile}>
          Update Profile
        </Button>
      </Form>
    </div>
  );
}

export default UserProfile;
