import React, { useState, useEffect } from "react";
import {
  Button,
  Form,
  Container,
  Row,
  Col,
  Card,
  Image,
} from "react-bootstrap";

import {
  signInWithEmailAndPassword,
  signInWithPopup,
  signOut,
} from "firebase/auth";


import { useNavigate } from "react-router-dom";

import { auth, googleProvider, } from "../config/firebase";

import axios from "axios";

const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, setUser] = useState(null);
  const [userProfile, setUserProfile] = useState({});
  const [userComments, setUserComments] = useState([]);
  const [userThreads, setUserThreads] = useState([]);

  const navigate = useNavigate();

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, async (currentUser) => {
      if (currentUser) {
        setUser(currentUser);

        try {
          // Here, using currentUser.uid to get the id of the user.
          const response = await axios.get(`/api/users/${currentUser.uid}`);
          setUserProfile(response.data);
        } catch (error) {
          console.error("Error fetching user data:", error);
        }

        // You could also fetch user comments and threads here, if needed
      } else {
        setUser(null);
        setUserProfile({});
      }
    });

    // Cleanup the subscription on component unmount
    return () => unsubscribe();
  }, []);
  useEffect(() => {
    const fetchUserData = async () => {
      if (user) {
        try {
          const response = await axios.get(`/api/users/${user.id}`);

          setUserProfile(response.data);

          const commentsResponse = await axios.get(
            `/api/users/${user.id}/comments`
          );
          setUserComments(commentsResponse.data);

          const threadsResponse = await axios.get(
            `/api/users/${user.id}/threads`
          );
          setUserThreads(threadsResponse.data);
        } catch (error) {
          console.error("Error fetching user data:", error);
        }
      }
    };

    fetchUserData();
  }, [user]);

  const handleSignIn = async () => {
    try {
      const loginResponse = await signInWithEmailAndPassword(
        auth,
        email,
        password
      );

      // redirect to landing page
      navigate("/landing");
      // setUser(response.data);
    } catch (error) {
      console.error("Error signing in:", error);
    }
  };

  const handleSignInWithGoogle = async () => {
    try {
      await signInWithPopup(auth, googleProvider);
    } catch (error) {
      console.error("Error signing in with Google:", error);
    }
  };




  const handleSignOut = async () => {
    try {
      await axios.post("/api/auth/logout");
      setUser(null);
    } catch (error) {
      console.error("Error signing out:", error);
    }
  };

  return (
    <Container fluid className="p-0">
      <Row noGutters className="min-vh-100">
        <Col
          md={6}
          className="d-flex align-items-center justify-content-center p-5"
        >
          <Card className="w-100 p-4">
            <Card.Title className="text-center">
              <Image
                src="./assets/ProgradeLogo.png"
                roundedCircle
                width="80"
                height="80"
                className="mb-3"
              />
              Welcome to Prograde
            </Card.Title>
            {user ? (
              <>
                <img src={userProfile.profilePhoto} alt="Profile" />
                <h3>Your Comments:</h3>
                <ul>
                  {userComments.map((comment, index) => (
                    <li key={index}>{comment.text}</li>
                  ))}
                </ul>
                <h3>Your Threads:</h3>
                <ul>
                  {userThreads.map((thread, index) => (
                    <li key={index}>{thread.title}</li>
                  ))}
                </ul>
                <Button variant="danger" onClick={handleSignOut}>
                  Logout
                </Button>
              </>
            ) : (
              <Form>
                <Form.Group controlId="email">
                  <Form.Label>Email address</Form.Label>
                  <Form.Control
                    type="email"
                    placeholder="Enter email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                </Form.Group>

                <Form.Group controlId="password">
                  <Form.Label>Password</Form.Label>
                  <Form.Control
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                  />
                </Form.Group>
                <Button variant="secondary" onClick={handleSignIn}>
                  Sign In
                </Button>
                <Button
                  variant="primary"
                  onClick={handleSignInWithGoogle}
                  className="ml-2"
                >
                  Sign In with Google
                </Button>
              </Form>
            )}
          </Card>
        </Col>
        <Col md={6} className="d-none d-md-block p-0">
          <Image src="./assets/commute.jpg" alt="Commute Image" fluid />
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
