import React, { useEffect, useState } from "react";
import Navbar from './Components/Navbar';
import CommentsList from './Components/CommentsList';
import "./App.css";
import { Auth } from "./Components/Auth";
import { db, auth, storage } from "./config/firebase";
import {
  getDocs,
  collection,
  addDoc,
  deleteDoc,
} from "firebase/firestore";
import { ref, uploadBytes } from "firebase/storage";
import { Button, Form, InputGroup } from "react-bootstrap";
import { Link } from "react-router-dom";

function App() {
  const [commentList, setCommentList] = useState([]);
  const [newCommentContent, setNewCommentContent] = useState("");
  const [fileUpload, setFileUpload] = useState(null);

  const commentsCollectionRef = collection(db, "comments");

  useEffect(() => {
    const fetchCommentList = async () => {
      try {
        const data = await getDocs(commentsCollectionRef);
        const filteredData = data.docs.map((doc) => ({
          ...doc.data(),
          id: doc.id,
        }));
        setCommentList(filteredData);
      } catch (err) {
        console.error(err);
      }
    };

    fetchCommentList();
  }, []);

  const onSubmitComment = async () => {
    const user = auth.currentUser;

    if (user) {
      const userId = user.uid;
      try {
        if (newCommentContent.trim() === "") {
          console.error("Comment content is empty.");
          return;
        }

        await addDoc(commentsCollectionRef, {
          text: newCommentContent,
          userId: userId,
          threadId: "YourThreadId",  // replace with actual thread ID 
          likes: 0,
          timestamp: new Date()
        });

        setCommentList((prevCommentList) => [
          ...prevCommentList,
          {
            text: newCommentContent,
            userId: userId,
            threadId: "YourThreadId",  // replace with actual thread ID
            likes: 0,
            timestamp: new Date()
          },
        ]);
        setNewCommentContent("");
      } catch (err) {
        console.error(err);
      }
    } else {
      console.error("User is not authenticated.");
    }
  };

  const deleteComment = async (id) => {
    const commentDoc = doc(db, "comments", id);
    await deleteDoc(commentDoc);
    setCommentList((prevCommentList) =>
      prevCommentList.filter((comment) => comment.id !== id)
    );
  };

  const updateCommentContent = (id, updatedContent) => {
    setCommentList((prevCommentList) =>
      prevCommentList.map((comment) =>
        comment.id === id
          ? { ...comment, text: updatedContent }
          : comment
      )
    );
  };

  const uploadProfilePhoto = async () => {
    if (!fileUpload) return;
    const user = auth.currentUser;
    if (user) {
      const userId = user.uid;
      const photoRef = ref(storage, `profilePhotos/${userId}`);
      try {
        await uploadBytes(photoRef, fileUpload);
      } catch (err) {
        console.error(err);
      }
    } else {
      console.error("User is not authenticated.");
    }
  };

  return (
    <div className="App">
      <Navbar />
      <Auth />
      <div>
        <InputGroup className="mb-3">
          <Form.Control
            placeholder="Comment content..."
            value={newCommentContent}
            onChange={(e) => setNewCommentContent(e.target.value)}
          />
        </InputGroup>
        <Button onClick={onSubmitComment}>Submit Comment</Button>
        <Link to="/comments">View Comments</Link>
      </div>
      <CommentsList comments={commentList} onDelete={deleteComment} onUpdate={updateCommentContent} />
      <div>
        <Form.Control
          type="file"
          onChange={(e) => setFileUpload(e.target.files[0])}
        />
        <Button onClick={uploadProfilePhoto}>Upload Profile Photo</Button>
      </div>
    </div>
  );
}

export default App;
